INSERT INTO podcasts VALUES 2, '- The Naked Scientists Podcast - Stripping Down Science', 'http://www.thenakedscientists.com/naked_scientists_podcast.xml', '2014-1-9 20:21:10', 'The Naked Scientists flagship science show brings you a lighthearted look at the latest scientific breakthroughs, interviews with the world top scientists, answers to your science questions and science experiments to try at home.', 'http://www.podcastpedia.org/podcasts/792/-The-Naked-Scientists-Podcast-Stripping-Down-Science';
INSERT INTO podcasts VALUES 3, 'NPR: TED Radio Hour Podcast', 'http://www.npr.org/rss/podcast.php?id=510298', '2014-1-9 20:21:10', 'The TED Radio Hour is a journey through fascinating ideas: astonishing inventions, fresh approaches to old problems, new ways to think and create. Based on Talks given by riveting speakers on the world-renowned TED stage, each show is centered on a common theme - such as the source of happiness, crowd-sourcing innovation, power shifts, or inexplicable connections. The TED Radio Hour is hosted by Guy Raz, and is a co-production of NPR & TED. Follow the show @TEDRadioHour.', 'http://www.podcastpedia.org/podcasts/1183/NPR-TED-Radio-Hour-Podcast';
